public boolean updateWorkflowMainTable( String tablename,int id, Map<String, String > paramMap) throws Exception {
    if (paramMap != null && paramMap.size() > 0) {
        RecordSet rs = new RecordSet();
        List<Object> param = new ArrayList<>();
        String sql = "update " + tablename + " set ";
        // 遍历要更新的字段
        for (String key : paramMap.keySet()) {
            param.add(paramMap.get(key));
            sql += key + " = ? ,";
        }
        // 更新（最后的逗号要处理）
        sql = sql.substring(0, sql.length() - 1) + " where 1=1 ";
        if (id > 0) {
            sql += " and id = ? ";
            param.add(id);
        }
        return rs.executeUpdate(sql, param);
    }
    return false;
}

public boolean insertToDtTable (String tablename, Map < String, String > paramMap) throws Exception {
    if (paramMap != null && paramMap.size() > 0) {
        RecordSet rs = new RecordSet();
        List<Object> param = new ArrayList<>();
        String sql = "insert into " + tablename + " ( ";
        // 遍历要插入的字段
        for (String key : paramMap.keySet()) {
            param.add(paramMap.get(key));
            sql += key + " ,";
        }
        // 最后的逗号要处理
        sql = sql.substring(0, sql.length() - 1) + " ) values ( ";

        for (String key : paramMap.keySet()) {
            sql += " ? ,";
        }
        sql = sql.substring(0, sql.length() - 1) + " )";
        return rs.executeUpdate(sql, param);
    }
    return false;
}

public List<Map<String, String>> getDetailData(String tablename, int requestid) {
    //log.info("getDetailData::tablename = [{}], requestid = [{}]", tablename, requestid);
    List<Map<String, String>> result = new ArrayList<>();
    RecordSet rs = new RecordSet();
    String mainTable = tablename.split("_dt")[0];
    String sql = " SELECT t2.* FROM " + mainTable + " t1\n" +
            "  LEFT JOIN " + tablename + " t2 ON t1.id = t2.MAINID \n" +
            " WHERE REQUESTID  = " + requestid;
    rs.execute(sql);
    log.info(sql);
    // 获取查询到的列
    String[] columnName = rs.getColumnName();
    log.info("colName{}", ArrayUtil.toString(columnName));
    // 获取结果集合
    Vector array = rs.getArray();

    //遍历结果集
    for (int i = 0; i < array.size(); i++) {
        Map<String, String> item = new HashMap<>();
        // 获取每一行的数据数组
        Object[] row = (Object[]) ((Object[]) array.get(i));
        log.info("row{}", ArrayUtil.toString(row));

        // 将列名和对应数据 转化为 一个Map
        for (int j = 0, rowLength = row.length; j < rowLength; j++) {
            Object cell = row[j];
            item.put(columnName[j].toLowerCase(), cell.toString());
        }
        result.add(item);
    }
    return result;
}
